function submit() {
	(document.getElementById("result").innerHTML =
		"<img src='https://media0.giphy.com/media/3oEjI6SIIHBdRxXI40/200.gif'>"),
		fetch(
			document.getElementById("url").value +
				"/wp-json/wp/v2/search?search=" +
				encodeURIComponent(document.getElementById("query").value),
		)
			.then((e) => e.text())
			.then((e) => {
				document.getElementById("result").innerHTML = "";
				var t = 0;
				JSON.parse(e).forEach((e) => {
					fetch(document.getElementById("url").value + "/wp-json/wp/v2/posts/" + e.id)
						.then((e) => e.text())
						.then((e) => {
							(document.getElementById("result").innerHTML += `<tr><td class='odd'>${
								JSON.parse(e).title.rendered
							}</td><td class='even' id='content${t}'></td><td class='odd'>${
								JSON.parse(e).content.rendered
							}</td><td class='even'>${JSON.parse(e).date}</td></tr>`),
								(document.getElementById(`content${t}`).innerText = JSON.parse(e).content.rendered),
								t++;
						});
				}),
					(document.body.innerHTML += "<style>table{border: 5px #000 solid;}</style>");
			});
}
